import React from 'react';
import { Pie } from 'react-chartjs-2';
import 'chart.js/auto';
import { Chart as ChartJS } from 'chart.js/auto';

function GenderCategoryRanking({ data }) {
  // 데이터 포맷
  const chartData = {
    labels: data.map(item => item.gender),  // 성별 라벨
    datasets: [
      {
        data: data.map(item => item.sales),  // 판매액 데이터
        backgroundColor: ['#FF6384', '#36A2EB'],  // 성별에 따른 색상
        hoverBackgroundColor: ['#FF6384', '#36A2EB'],
      },
    ],
  };

  return (
    <div className="chart">
      <h3>성별 카테고리 판매 순위</h3>
      <Pie data={chartData} />
    </div>
  );
}

export default GenderCategoryRanking;
