import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminPage.css';

function AdminPage() {
  const [salesData, setSalesData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSalesData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/admin/", {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`  // Authorization 헤더에 토큰 추가
          }
        });
        setSalesData(response.data);  // 데이터를 받아오는 코드 (현재 이 데이터를 사용하지 않음)
      } catch (err) {
        setError("데이터를 불러오는 중 오류가 발생했습니다.");
        console.error(err);  // 오류 로그 출력
      }
    };

    fetchSalesData();  // 컴포넌트가 처음 렌더링될 때 API 호출
  }, []);  // 의존성 배열이 빈 배열이므로 한 번만 실행

  if (error) {
    return <div>{error}</div>;  // 오류 발생 시 오류 메시지 출력
  }

  return (
    <div>
      <h1>관리자 페이지</h1>
      <p>관리자 페이지에 오신 것을 환영합니다.</p>
    </div>
  );
}

export default AdminPage;
