import React from 'react';

// props로 1~6월 판매량과 예측된 7월 판매량을 받습니다.
const SalesPrediction = ({ salesData, predictedSales }) => {
  return (
    <div>
      <h1>판매 예측</h1>
      {salesData && predictedSales !== null ? (
        <>
          <h3>2025년 1~6월 판매량</h3>
          <ul>
            {salesData.map((sales, index) => (
              <li key={index}>
                {index + 1}월: {sales.toLocaleString()} 원
              </li>
            ))}
          </ul>

          <h3>예측된 7월 판매량</h3>
          <p>{predictedSales.toLocaleString()} 원</p>
        </>
      ) : (
        <p>판매 예측을 불러오는 중입니다...</p> // 로딩 상태
      )}
    </div>
  );
}

export default SalesPrediction;
