// src/components/Header.js
import React, { useContext } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import './Header.css';
import { AuthContext } from '../context/AuthContext';

const categories = [
  "Í≥†Í∏âÎ≥ºÌéú",
  "Ïú†ÏÑ±Î≥ºÌéú",
  "ÏÉ§ÌîÑ",
  "ÏÉ§ÌîÑÏã¨",
  "ÎÖ∏Ìä∏",
  "ÏàòÏ†ïÏö©Ìíà",
];

function Header() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <header className="header">
      <h1 className="logo">üõí Monami Mall</h1>

      {/* Í∏∞Ï°¥ ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò Î©îÎâ¥ */}
      <nav>
        <ul className="nav-list">
          <li><NavLink to="/" end>Ìôà</NavLink></li>
          <li><NavLink to="/cart">Ïû•Î∞îÍµ¨Îãà</NavLink></li>
          <li><NavLink to="/checkout">Í≤∞Ï†ú</NavLink></li>

          {!user && (
            <>
              <li><NavLink to="/login">Î°úÍ∑∏Ïù∏</NavLink></li>
              <li><NavLink to="/register">ÌöåÏõêÍ∞ÄÏûÖ</NavLink></li>
            </>
          )}

          {user && (
            <>
              {user.role === 'admin' && (
                <li><NavLink to="/admin">Í¥ÄÎ¶¨Ïûê</NavLink></li>
              )}
              <li>
                <button onClick={handleLogout} className="logout-button">
                  Î°úÍ∑∏ÏïÑÏõÉ
                </button>
              </li>
            </>
          )}
        </ul>
      </nav>

      {/* Ïπ¥ÌÖåÍ≥†Î¶¨ Î©îÎâ¥ Î≥ÑÎèÑ ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò */}
      <nav className="category-nav" style={{ marginTop: '10px' }}>
        <ul className="nav-list category-list" style={{ display: 'flex', gap: '15px', paddingLeft: 0 }}>
          {categories.map((cat) => (
            <li key={cat}>
              <NavLink
                to={`/category/${encodeURIComponent(cat)}`}
                style={({ isActive }) => ({
                  fontWeight: isActive ? 'bold' : 'normal',
                  textDecoration: 'none',
                  color: isActive ? '#007bff' : 'inherit',
                })}
              >
                {cat}
              </NavLink>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
}

export default Header;
