import React, { useState } from 'react';
import Tesseract from 'tesseract.js';

function OcrUploader() {
  const [ocrResult, setOcrResult] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setIsProcessing(true); // OCR 시작 중
      const reader = new FileReader();

      reader.onload = (e) => {
        const image = e.target.result;
        
        // Tesseract.js로 OCR 처리
        Tesseract.recognize(
          image,
          'eng', // 사용할 OCR 언어 (예: 'eng' 영어)
          {
            logger: (m) => console.log(m), // 진행 상태 로깅
          }
        ).then(({ data: { text } }) => {
          setOcrResult(text); // OCR 결과 저장
          setIsProcessing(false); // OCR 완료
        }).catch((error) => {
          console.error("OCR 처리 중 오류 발생:", error);
          setIsProcessing(false);
        });
      };

      reader.readAsDataURL(file); // 이미지 파일 읽기
    }
  };

  return (
    <div className="ocr-uploader-container">
      <h4>OCR 이미지 업로드</h4>
      <input type="file" accept="image/*" onChange={handleFileUpload} />
      {isProcessing && <p>OCR 분석 중...</p>}

      {ocrResult && (
        <div>
          <h5>OCR 분석 결과:</h5>
          <pre>{ocrResult}</pre>
        </div>
      )}
    </div>
  );
}

export default OcrUploader;
