import React, { useState } from 'react';
import './OrderDetails.css';  // 스타일을 별도의 파일로 관리

function OrderDetails({ data }) {
  const [selectedOrder, setSelectedOrder] = useState(null);

  const handleShowDetails = (orderId) => {
    const order = data.find(order => order.orderId === orderId);
    setSelectedOrder(order);
  };

  return (
    <div className="order-details-container">
      <h4>주문 내역</h4>
      {data.map(order => (
        <div key={order.orderId} className={`order-card ${order.status.toLowerCase()}`}>
          <p>주문 ID: {order.orderId}</p>
          <p>사용자 ID: {order.userId}</p>
          <p>주문 날짜: {order.orderDate}</p>
          <p>상태: {order.status}</p>
          <p>총 금액: {order.totalAmount}원</p>
          <button onClick={() => handleShowDetails(order.orderId)}>세부 정보 보기</button>
        </div>
      ))}

      {selectedOrder && (
        <div className="order-details-modal">
          <h5>주문 세부 정보</h5>
          <p><strong>주문 ID:</strong> {selectedOrder.orderId}</p>
          <p><strong>사용자 ID:</strong> {selectedOrder.userId}</p>
          <p><strong>주문 날짜:</strong> {selectedOrder.orderDate}</p>
          <p><strong>상태:</strong> {selectedOrder.status}</p>
          <p><strong>총 금액:</strong> {selectedOrder.totalAmount}원</p>
          <p><strong>배송 주소:</strong> {selectedOrder.deliveryAddress}</p>
          <button onClick={() => setSelectedOrder(null)}>닫기</button>
        </div>
      )}
    </div>
  );
}

export default OrderDetails;
