import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import MonthlySalesChart from './MonthlySalesChart';  // 월별 판매 차트
import MonthlyOrdersChart from './MonthlyOrdersChart'; // 월별 주문 건수 차트
import CategorySalesChart from './CategorySalesChart'; // 카테고리별 판매 차트
import GrowthRate from './GrowthRate'; // 성장률 차트
import AgeCategoryRanking from './AgeCategoryRanking'; // 연령대 카테고리 순위 차트
import GenderCategoryRanking from './GenderCategoryRanking'; // 성별 카테고리 순위 차트
import SalesPrediction from './SalesPrediction'; // 판매 예측 차트
import SalesStrategy from './SalesStrategy'; // 판매 전략 차트
import './DashboardPage.css';  // CSS 스타일

function DashboardPage() {
  const [dashboardData, setDashboardData] = useState(null);
  const [error, setError] = useState(null);
  const [selectedMenu, setSelectedMenu] = useState('월별 판매액'); // 기본 선택 메뉴
  const year = 2025; // 조회 연도

  // 메뉴 URL 맵 (월별 판매액, 주문 건수, 카테고리별 판매 수량 등 파라미터 필요)
  const menuUrls = useMemo(() => ({
    '월별 판매액': '/admin/monthly-sales',
    '월별 주문 건수': '/admin/monthly-orders',
    '카테고리별 판매 수량': '/admin/category-sales',
    '성장률': '/admin/growth-rate',
    '연령대별 카테고리 판매 순위': '/admin/age-category-ranking',
    '성별 카테고리 판매 순위': '/admin/gender-category-ranking',
    '판매 예측': '/admin/sales-prediction',
    '판매 전략': '/admin/sales-strategy',
  }), []);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        let url = `http://localhost:8080${menuUrls[selectedMenu]}`;

        // 1월~6월 기간 파라미터 필요한 메뉴들
if (
  selectedMenu === '월별 판매액' ||
  selectedMenu === '월별 주문 건수' ||
  selectedMenu === '카테고리별 판매 수량' ||
  selectedMenu === '성장률' ||
  selectedMenu === '연령대별 카테고리 판매 순위' ||
  selectedMenu === '성별 카테고리 판매 순위'
) {
  url += `?year=${year}&startMonth=1&endMonth=6`;
}



        console.log('Fetching data from URL:', url);
        const response = await axios.get(url, {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
        });
        console.log('API Response:', response.data);
        setDashboardData(response.data);
      } catch (err) {
        console.error('Error fetching data:', err);
        setError('데이터를 불러오는 중 오류가 발생했습니다.');
      }
    };

    if (selectedMenu) {
      fetchDashboardData();
    }
  }, [selectedMenu, year, menuUrls]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!dashboardData) {
    return <div>로딩 중...</div>;
  }

  return (
    <div className="dashboard-page-container">
      <h2>관리자 대시보드</h2>

      {/* 네비게이션 메뉴 */}
      <nav>
        <ul>
          {Object.keys(menuUrls).map(menuItem => (
            <li key={menuItem}>
              <button
                onClick={() => setSelectedMenu(menuItem)}
                className={selectedMenu === menuItem ? 'active' : ''}
              >
                {menuItem}
              </button>
            </li>
          ))}
        </ul>
      </nav>

      {/* 선택된 메뉴에 따른 콘텐츠 */}
      <div className="chart-container">
        {selectedMenu === '월별 판매액' && (
          <>
            <h3>월별 판매 통계 (1월~6월)</h3>
            {Array.isArray(dashboardData) && dashboardData.length > 0 ? (
              <MonthlySalesChart data={dashboardData} />
            ) : (
              <div>데이터가 없습니다.</div>
            )}
          </>
        )}

        {selectedMenu === '월별 주문 건수' && (
          <>
            <h3>월별 주문 건수</h3>
            {Array.isArray(dashboardData) && dashboardData.length > 0 ? (
              <MonthlyOrdersChart data={dashboardData} />
            ) : (
              <div>로딩 중...</div>
            )}
          </>
        )}

        {selectedMenu === '카테고리별 판매 수량' && (
          <>
            <h3>2025년 1월~6월 카테고리별 판매 통계</h3>
            {Array.isArray(dashboardData) && dashboardData.length > 0 ? (
              <CategorySalesChart data={dashboardData} />
            ) : (
              <div>로딩 중...</div>
            )}
          </>
        )}

{selectedMenu === '성장률' && (
  <>
    <h3>성장률</h3>
    {Array.isArray(dashboardData) && dashboardData.length > 0 ? (
      <GrowthRate data={dashboardData} />
    ) : (
      <div>로딩 중...</div>
    )}
  </>
)}


{selectedMenu === '연령대별 카테고리 판매 순위' && (
  <>
    <h3>연령대별 카테고리 판매 순위</h3>
    {Array.isArray(dashboardData) && dashboardData.length > 0 ? (
      <AgeCategoryRanking data={dashboardData} />
    ) : (
      <div>로딩 중...</div>
    )}
  </>
)}

{selectedMenu === '성별 카테고리 판매 순위' && (
  <>
    <h3>성별 카테고리 판매 순위</h3>
    {Array.isArray(dashboardData) && dashboardData.length > 0 ? (
      <GenderCategoryRanking data={dashboardData} />
    ) : (
      <div>로딩 중...</div>
    )}
  </>
)}

        {selectedMenu === '판매 예측' && (
          <>
            <h3>판매 예측</h3>
            {dashboardData.salesPrediction ? (
              <SalesPrediction data={dashboardData.salesPrediction} />
            ) : (
              <div>로딩 중...</div>
            )}
          </>
        )}

        {selectedMenu === '판매 전략' && (
          <>
            <h3>판매 전략</h3>
            {dashboardData.salesStrategy ? (
              <SalesStrategy data={dashboardData.salesStrategy} />
            ) : (
              <div>로딩 중...</div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default DashboardPage;
