import React from 'react';

function MonthlySalesChart({ data }) {
  if (!data) {
    return <div>데이터를 불러오는 중입니다...</div>;
  }

  return (
    <div>
      <h4>{data.year}년 {data.month}월 판매 요약</h4>
      <p>총 판매액: {data.totalSales.toLocaleString()}원</p>
    </div>
  );
}

export default MonthlySalesChart;
