import React, { useState } from 'react';
import './OrderDetails.css';

function OrderDetails({ data }) {
  const [selectedMonth, setSelectedMonth] = useState(null);

  const handleMonthFilter = (month) => {
    setSelectedMonth(month);
  };

  // 월별로 필터링된 주문 데이터
  const filteredData = selectedMonth 
    ? data.filter(order => new Date(order.orderDate).getMonth() + 1 === selectedMonth)
    : data;

  return (
    <div className="order-details-container">
      <div className="month-buttons">
        {['1', '2', '3', '4', '5', '6'].map((month) => (
          <button key={month} onClick={() => handleMonthFilter(Number(month))}>
            {month}월
          </button>
        ))}
        <button onClick={() => setSelectedMonth(null)}>전체 보기</button>
      </div>
      
      <h4>주문 내역</h4>
      {filteredData.length === 0 ? (
        <p>해당 월의 주문 내역이 없습니다.</p>
      ) : (
        <div className="order-list">
          {filteredData.map(order => (
            <div key={order.orderId} className={`order-card ${order.status.toLowerCase()}`}>
              <p>주문 ID: {order.orderId}</p>
              <p>사용자 ID: {order.userId}</p>
              <p>주문 날짜: {order.orderDate}</p>
              <p>상태: {order.status}</p>
              <p>총 금액: {order.totalAmount}원</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default OrderDetails;
