import React from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';

function SalesPrediction({ data }) {
  const chartData = {
    labels: data.map(item => item.month), // 월별 레이블
    datasets: [
      {
        label: '예상 판매액',
        data: data.map(item => item.predictedSales), // 예상 판매액 데이터
        fill: false,
        borderColor: 'rgb(255, 159, 64)',
        tension: 0.1,
      },
    ],
  };

  return (
    <div className="chart">
      <h3>판매 예측</h3>
      <Line data={chartData} />
    </div>
  );
}

export default SalesPrediction;
