import axiosInstance from './axiosInstance';

const AUTH_BASE = '/auth';

export const login = async ({ email, password }) => {
  try {
    const response = await axiosInstance.post(`${AUTH_BASE}/login`, { email, password });

    const { token, user } = response.data;

    localStorage.setItem('token', token); // 로그인 시 토큰 저장

    return { user, token };  // user와 token을 반환
  } catch (error) {
    console.error('Login error:', error);
    throw new Error(error.response ? error.response.data.message : '서버와의 연결에 실패했습니다.');
  }
};

export const register = async (userData) => {
  try {
    const response = await axiosInstance.post(`${AUTH_BASE}/register`, userData);
    return response.data;
  } catch (error) {
    throw new Error(error.response ? error.response.data : '회원가입 중 오류가 발생했습니다.');
  }
};

export const logout = () => {
  localStorage.removeItem('token');
};

export const getCurrentUser = async () => {
  try {
    const response = await axiosInstance.get(`${AUTH_BASE}/me`);
    return response.data;
  } catch (error) {
    throw new Error('사용자 정보를 불러오는 중 오류가 발생했습니다.');
  }
};
