import React, { useState } from 'react';
import { register } from '../api/auth';  // 회원가입 API 함수 (이미 있다고 가정)
import { validateEmail, validatePassword } from '../utils/validation';
import { useNavigate } from 'react-router-dom';
import './RegisterPage.css';

const RegisterPage = () => {
  const navigate = useNavigate();

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [birthdate, setBirthdate] = useState('');
  const [gender, setGender] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name.trim()) {
      setError('이름을 입력하세요.');
      return;
    }
    if (!validateEmail(email)) {
      setError('유효한 이메일을 입력하세요.');
      return;
    }
    if (!validatePassword(password)) {
      setError('비밀번호는 6자 이상이어야 합니다.');
      return;
    }
    if (!/^\d{4}-\d{2}-\d{2}$/.test(birthdate)) {
      setError('생년월일은 xxxx-xx-xx 형식으로 입력하세요.');
      return;
    }
    if (!['m', 'f'].includes(gender.toLowerCase())) {
      setError('성별은 "m" 또는 "f"로 입력하세요.');
      return;
    }

    try {
      await register({ name, email, password, birthdate, gender });
      alert('회원가입이 완료되었습니다.');
      navigate('/login');
    } catch (err) {
      setError('회원가입 실패: 정보를 확인하세요.');
    }
  };

  return (
    <div className="register-container">
      <h2>회원가입</h2>
      <form onSubmit={handleSubmit} className="register-form">
        <input
          type="text"
          placeholder="이름"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="이메일"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="비밀번호"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="생년월일 (예: 1990-01-01)"
          value={birthdate}
          onChange={(e) => setBirthdate(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder='성별 (m 또는 f)'
          value={gender}
          onChange={(e) => setGender(e.target.value)}
          required
          maxLength={1}
        />
        {error && <p className="error-message">{error}</p>}
        <button type="submit">회원가입</button>
      </form>
    </div>
  );
};

export default RegisterPage;
