const chartData = {
  labels: data.map((item) => item.month),
  datasets: [
    {
      label: '월별 주문 건수',
      data: data.map((item) => item.totalSales),  // orders → totalSales로 변경
      borderColor: '#ff7043',
      backgroundColor: 'rgba(255, 112, 67, 0.2)',
      fill: true,
    },
  ],
};
