import React from 'react';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';

const CategorySalesChart = ({ data }) => {
  if (!data || data.length === 0) {
    return <div>로딩 중...</div>;
  }

  const chartData = {
    labels: data.map(item => item.category),  // 카테고리 이름들
    datasets: [
      {
        label: '카테고리별 판매액',
        data: data.map(item => item.sales),    // 각 카테고리별 판매액
        backgroundColor: 'rgba(54, 162, 235, 0.5)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    scales: {
      x: {
        beginAtZero: true,
        title: {
          display: true,
          text: '카테고리',
        },
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: '판매액',
        },
      },
    },
    plugins: {
      tooltip: {
        enabled: true,
      },
      legend: {
        position: 'top',
      },
    },
  };

  return (
    <div className="chart-container">
      <h3>카테고리별 판매 통계</h3>
      <Bar data={chartData} options={options} />
    </div>
  );
};

export default CategorySalesChart;
