import React, { useState } from 'react';
import { createOrder, processPayment } from '../api/payment';
import OCRUploader from '../components/OCRUploader';
import './CheckoutPage.css';

function CheckoutPage() {
  const [form, setForm] = useState({
    name: '',
    address: '',
    cardNumber: '',
    expiry: '',
    cvc: '',
  });
  const [loading, setLoading] = useState(false);
  const [orderSuccess, setOrderSuccess] = useState(false);
  const [error, setError] = useState(null);
  const [ocrResult, setOcrResult] = useState(null);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleOCRComplete = (text) => {
    setOcrResult(text);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // 1. 결제 처리
      const paymentRes = await processPayment({
        cardNumber: form.cardNumber,
        expiry: form.expiry,
        cvc: form.cvc,
        amount: 100, // 예시 금액 (실제 금액 계산 필요)
      });

      // 2. 주문 생성
      const orderRes = await createOrder({
        name: form.name,
        address: form.address,
        paymentId: paymentRes.id,
        ocrData: ocrResult,
      });

      setOrderSuccess(true);
    } catch (err) {
      setError('결제 또는 주문 처리 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  if (orderSuccess) {
    return <div>주문이 성공적으로 완료되었습니다! 감사합니다.</div>;
  }

  return (
    <div className="checkout-page">
      <h2>결제 및 주문</h2>
      <OCRUploader onComplete={handleOCRComplete} />
      <form onSubmit={handleSubmit} className="checkout-form">
        <label>
          이름:
          <input type="text" name="name" value={form.name} onChange={handleChange} required />
        </label>
        <label>
          배송 주소:
          <input type="text" name="address" value={form.address} onChange={handleChange} required />
        </label>
        <label>
          카드 번호:
          <input type="text" name="cardNumber" value={form.cardNumber} onChange={handleChange} required />
        </label>
        <label>
          만료일:
          <input type="text" name="expiry" value={form.expiry} onChange={handleChange} placeholder="MM/YY" required />
        </label>
        <label>
          CVC:
          <input type="text" name="cvc" value={form.cvc} onChange={handleChange} required />
        </label>
        <button type="submit" disabled={loading}>
          {loading ? '처리 중...' : '결제하기'}
        </button>
        {error && <p className="error">{error}</p>}
      </form>
    </div>
  );
}

export default CheckoutPage;
