import React from 'react';

function MonthlySalesChart({ data }) {
  if (!data) {
    return <div>데이터가 없습니다.</div>;
  }

  // data는 Spring에서 온 JSON 객체라고 가정
  // 예: { month: 10, totalSales: 2300000, ... }

  return (
    <div>
      <h4>10월 총 판매액: {data.totalSales.toLocaleString()}원</h4>
      {/* 여기에 차트를 넣거나, 분석 결과를 보여주는 로직 추가 */}
    </div>
  );
}

export default MonthlySalesChart;
