import React from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';

function GrowthRate({ data }) {
  // month를 '1월', '2월' 이런 식으로 변환
  const labels = data.map(item => `${item.currentMonth}월`);


  // growthRate를 소수점 2자리로 제한 (숫자형일 때만)
  const growthRates = data.map(item => {
    if (typeof item.growthRate === 'number') {
      return Number(item.growthRate.toFixed(2));
    }
    return item.growthRate; // 혹시 문자열 등일 경우 그대로 반환
  });

  const chartData = {
    labels: labels,
    datasets: [
      {
        label: '성장률 (%)',
        data: growthRates,
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
        pointBackgroundColor: 'rgb(75, 192, 192)',
        pointRadius: 5,
      },
    ],
  };

  const options = {
    scales: {
      y: {
        ticks: {
          // y축에 % 단위 표시
          callback: function(value) {
            return value + '%';
          },
        },
        beginAtZero: true,
      },
    },
    plugins: {
      tooltip: {
        callbacks: {
          label: function(context) {
            return `성장률: ${context.parsed.y}%`;
          },
        },
      },
    },
  };

  return (
    <div className="chart">

      <Line data={chartData} options={options} />
    </div>
  );
}

export default GrowthRate;
