import React from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';

const MonthlySalesChart = ({ data }) => {
  if (!data || data.length === 0) {
    return <div>로딩 중...</div>;  // 데이터가 없을 경우 로딩 중 표시
  }

  // 데이터가 있을 경우 차트 데이터 생성
  const chartData = {
    labels: data.map((item) => item.month), // 월을 라벨로 사용
    datasets: [
      {
        label: '월별 판매액',
        data: data.map((item) => item.sales), // 판매액을 데이터로 사용
        borderColor: '#42a5f5',
        backgroundColor: 'rgba(66, 165, 245, 0.2)',
        fill: true,  // 그래프 영역을 채우기
      },
    ],
  };

  const options = {
    responsive: true,
    scales: {
      x: {
        type: 'category',  // x축은 카테고리형으로 설정 (월 이름)
        beginAtZero: false,  // x축은 0에서 시작하지 않음
      },
      y: {
        beginAtZero: true,  // y축은 0에서 시작하도록 설정
      },
    },
    plugins: {
      tooltip: {
        enabled: true,  // 툴팁 활성화
      },
    },
  };

  return (
    <div className="chart-container">
      <h3>월별 판매 통계</h3>
      <Line data={chartData} options={options} />  {/* 차트 그리기 */}
    </div>
  );
};

export default MonthlySalesChart;
