import React from 'react';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';  

function AgeCategoryRanking({ data }) {
  // 카테고리별로 그룹화된 연령대 판매액 데이터를 가정
  const chartData = {
    labels: data.map(item => `${item.ageGroup}대`),  // 연령대(예: 10대, 20대)로 레이블 생성
    datasets: [
      {
        label: '판매액',
        data: data.map(item => item.sales),  // 판매액 데이터
        backgroundColor: 'rgba(75,192,192,0.5)',  // 배경 색상
        borderColor: 'rgba(75,192,192,1)',        // 테두리 색상
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="chart">
      <h3>연령대별 카테고리 판매 순위</h3>
      <Bar data={chartData} />
    </div>
  );
}

export default AgeCategoryRanking;
