import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';

function MonthlyOrdersChart({ data }) {
  const chartData = {
    labels: data.map(item => item.month), // 월별 레이블
    datasets: [
      {
        label: '주문 건수',
        data: data.map(item => item.orders), // 주문 건수 데이터
        fill: false,
        borderColor: 'rgb(153, 102, 255)',
        tension: 0.1,
      },
    ],
  };

  return (
    <div className="chart">
      <h3>월별 주문 건수</h3>
      <Line data={chartData} />
    </div>
  );
}

export default MonthlyOrdersChart;
