import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import MonthlySalesChart from './MonthlySalesChart';  // 월별 판매 차트
import MonthlyOrdersChart from './MonthlyOrdersChart'; // 월별 주문 건수 차트
import CategorySalesChart from './CategorySalesChart'; // 카테고리별 판매 차트
import GrowthRate from './GrowthRate'; // 성장률 차트
import AgeCategoryRanking from './AgeCategoryRanking'; // 연령대 카테고리 순위 차트
import GenderCategoryRanking from './GenderCategoryRanking'; // 성별 카테고리 순위 차트
import SalesPrediction from './SalesPrediction'; // 판매 예측 차트
import SalesStrategy from './SalesStrategy'; // 판매 전략 차트
import './DashboardPage.css';  // CSS 스타일

function DashboardPage() {
  const [dashboardData, setDashboardData] = useState(null);
  const [error, setError] = useState(null);
  const [selectedMenu, setSelectedMenu] = useState('월별 판매액'); // 기본적으로 선택된 메뉴

  const month = 10; // 예시: 10월
  const year = 2025; // 예시: 2025년

  // startDate와 endDate를 해당 연월의 시작일과 종료일로 생성 (ISO 8601 형식)
  const startDate = `${year}-${month.toString().padStart(2, '0')}-01T00:00:00`;
  // 해당 달의 마지막 날 계산 (간단히 31일로 하거나, 정확하게 계산 가능)
  // 여기서는 간단히 31일로 설정 (필요하면 라이브러리로 정확히)
  const endDate = `${year}-${month.toString().padStart(2, '0')}-31T23:59:59`;

  // 메뉴 URL을 useMemo로 감싸서, 불필요한 렌더링을 방지
  const menuUrls = useMemo(() => ({
    '월별 판매액': '/admin/monthly-sales',
    '월별 주문 건수': '/admin/monthly-orders',
    '카테고리별 판매 수량': '/admin/category-sales',
    '성장률': '/admin/growth-rate',
    '연령대별 카테고리 판매 순위': '/admin/age-category-ranking',
    '성별 카테고리 판매 순위': '/admin/gender-category-ranking',
    '판매 예측': '/admin/sales-prediction',
    '판매 전략': '/admin/sales-strategy',
  }), []);  // 빈 배열을 넣어 menuUrls가 변경되지 않음을 보장

  // 선택된 메뉴에 맞는 데이터 가져오기
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        // startDate, endDate를 쿼리 파라미터로 포함
        const url = `http://localhost:8080${menuUrls[selectedMenu]}`;
        console.log('Fetching data from URL:', url, { startDate, endDate });

        const response = await axios.get(url, {
          params: { startDate, endDate },
          headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
        });
        console.log('API Response:', response);  // API 응답 로그
        setDashboardData(response.data); // 데이터 설정
      } catch (err) {
        console.error('Error fetching data:', err);  // 에러 로그
        setError('데이터를 불러오는 중 오류가 발생했습니다.');
      }
    };

    if (selectedMenu) {
      fetchDashboardData();
    }
  }, [selectedMenu, startDate, endDate, menuUrls]); // startDate, endDate를 종속성 배열에 추가

  if (error) {
    return <div>{error}</div>;
  }

  if (!dashboardData) {
    return <div>로딩 중...</div>;
  }

  return (
    <div className="dashboard-page-container">
      <h2>관리자 대시보드</h2>

      {/* 네비게이션 메뉴 */}
      <nav>
        <ul>
          {Object.keys(menuUrls).map((menuItem) => (
            <li key={menuItem}>
              <button 
                onClick={() => setSelectedMenu(menuItem)} 
                className={selectedMenu === menuItem ? 'active' : ''}
              >
                {menuItem}
              </button>
            </li>
          ))}
        </ul>
      </nav>

      {/* 선택된 메뉴에 해당하는 콘텐츠 렌더링 */}
      <div className="chart-container">
        {selectedMenu === '월별 판매액' && (
          <>
            <h3>월별 판매 통계</h3>
            {dashboardData.monthlySales ? <MonthlySalesChart data={dashboardData.monthlySales} /> : <div>로딩 중...</div>}
          </>
        )}

        {selectedMenu === '월별 주문 건수' && (
          <>
            <h3>월별 주문 건수</h3>
            {dashboardData.monthlyOrders ? <MonthlyOrdersChart data={dashboardData.monthlyOrders} /> : <div>로딩 중...</div>}
          </>
        )}

        {selectedMenu === '카테고리별 판매 수량' && (
          <>
            <h3>카테고리별 판매 통계</h3>
            {dashboardData.categorySales ? <CategorySalesChart data={dashboardData.categorySales} /> : <div>로딩 중...</div>}
          </>
        )}

        {selectedMenu === '성장률' && (
          <>
            <h3>성장률</h3>
            {dashboardData.growthRate ? <GrowthRate data={dashboardData.growthRate} /> : <div>로딩 중...</div>}
          </>
        )}

        {selectedMenu === '연령대별 카테고리 판매 순위' && (
          <>
            <h3>연령대별 카테고리 판매 순위</h3>
            {dashboardData.ageCategoryRanking ? <AgeCategoryRanking data={dashboardData.ageCategoryRanking} /> : <div>로딩 중...</div>}
          </>
        )}

        {selectedMenu === '성별 카테고리 판매 순위' && (
          <>
            <h3>성별 카테고리 판매 순위</h3>
            {dashboardData.genderCategoryRanking ? <GenderCategoryRanking data={dashboardData.genderCategoryRanking} /> : <div>로딩 중...</div>}
          </>
        )}

        {selectedMenu === '판매 예측' && (
          <>
            <h3>판매 예측</h3>
            {dashboardData.salesPrediction ? <SalesPrediction data={dashboardData.salesPrediction} /> : <div>로딩 중...</div>}
          </>
        )}

        {selectedMenu === '판매 전략' && (
          <>
            <h3>판매 전략</h3>
            {dashboardData.salesStrategy ? <SalesStrategy data={dashboardData.salesStrategy} /> : <div>로딩 중...</div>}
          </>
        )}
      </div>
    </div>
  );
}

export default DashboardPage;
