import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SalesPrediction = () => {
    const [predictedSales, setPredictedSales] = useState(null);

    // useEffect(() => {
    //     // 7월 판매량 예측 API 호출
    //     axios.get('/api/sales/prediction/predict')
    //         .then(response => {
    //             setPredictedSales(response.data.predictedSales);
    //         })
    //         .catch(error => {
    //             console.error('Error fetching prediction:', error);
    //         });
    // }, []);

    return (
        <div>
            <h1>7월 판매 예측</h1>
            {predictedSales !== null ? (
                <p>예측된 7월 판매량: {predictedSales.toFixed(2)} 원</p>
            ) : (
                <p>판매 예측을 불러오는 중입니다...</p>
            )}
        </div>
    );
}

export default SalesPrediction;
