import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';  // AuthContext import

const NavigationBar = () => {
  const { user, logout } = useContext(AuthContext);  // 로그인한 사용자 정보와 logout 함수 가져오기

  return (
    <nav>
      <ul>
        <li>
          <Link to="/">홈</Link>
        </li>
        <li>
          <Link to="/cart">장바구니</Link>
        </li>
        <li>
          <Link to="/checkout">결제</Link>
        </li>

        {/* 관리자 로그인 여부에 따른 네비게이션 링크 표시 */}
        {user ? (
          // 사용자가 로그인된 경우
          <>
            <li>
              {user.role === 'admin' && <Link to="/admin/dashboardpage">관리자 대시보드</Link>}  {/* 관리자일 경우만 링크 표시 */}
            </li>
            <li>
              <button onClick={logout}>로그아웃</button>  {/* 로그아웃 버튼 */}
            </li>
            <li>
              <span>{user.email}님, 안녕하세요!</span>  {/* 로그인된 사용자 표시 */}
            </li>
          </>
        ) : (
          // 사용자가 로그인되지 않은 경우
          <>
            <li>
              <Link to="/login">로그인</Link>
            </li>
            <li>
              <Link to="/register">회원가입</Link>
            </li>
          </>
        )}
      </ul>
    </nav>
  );
};

export default NavigationBar;
