import React from 'react';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';

function SalesStrategy({ data }) {
  const chartData = {
    labels: data.map(item => item.strategy), // 판매 전략 레이블
    datasets: [
      {
        label: '매출 변동',
        data: data.map(item => item.salesChange), // 판매 전략에 따른 매출 변화 데이터
        backgroundColor: 'rgba(75,192,192,0.5)',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="chart">
      <h3>판매 전략</h3>
      <Bar data={chartData} />
    </div>
  );
}

export default SalesStrategy;
