import React from 'react';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';

function AgeCategoryRanking({ data }) {
  // 데이터 포맷에 맞춰 차트 데이터 준비
  const chartData = {
    labels: data.map(item => `${item.ageGroup}대`),  // 연령대 (10대, 20대 등)
    datasets: [
      {
        label: '카테고리 A 판매량',
        data: data.filter(item => item.category === 'A').map(item => item.sales),
        backgroundColor: 'rgba(255,99,132,0.5)',
        borderColor: 'rgba(255,99,132,1)',
        borderWidth: 1,
      },
      {
        label: '카테고리 B 판매량',
        data: data.filter(item => item.category === 'B').map(item => item.sales),
        backgroundColor: 'rgba(54,162,235,0.5)',
        borderColor: 'rgba(54,162,235,1)',
        borderWidth: 1,
      },
      // 다른 카테고리도 추가 가능
    ],
  };

  return (
    <div className="chart">
      <h3>연령대별 카테고리 판매 순위</h3>
      <Bar data={chartData} />
    </div>
  );
}

export default AgeCategoryRanking;
