import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SalesPrediction = () => {
    const [predictedSales, setPredictedSales] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        // API 호출 URL을 절대 경로로 수정 (개발 중엔 localhost 사용)
        axios.get('http://localhost:8080/api/sales/prediction/predict')
            .then(response => {
                setPredictedSales(response.data.predictedSales);
            })
            .catch(error => {
                console.error('Error fetching prediction:', error);
                setError('판매 예측을 불러오는 중 오류가 발생했습니다.');
            });
    }, []);

    return (
        <div>
            <h1>7월 판매 예측</h1>
            {error ? (
                <p>{error}</p> // 에러 메시지를 사용자에게 표시
            ) : predictedSales !== null ? (
                <p>예측된 7월 판매량: {predictedSales.toLocaleString()} 원</p> // 숫자 포맷팅 (천단위 쉼표)
            ) : (
                <p>판매 예측을 불러오는 중입니다...</p> // 로딩 상태
            )}
        </div>
    );
}

export default SalesPrediction;
