import React from 'react';
import { Line } from 'react-chartjs-2'; // Line 차트 컴포넌트를 사용

const MonthlySalesChart = ({ data }) => {
  if (!data) {
    return <div>로딩 중...</div>;
  }

  const chartData = {
    labels: data.map((item) => item.month),
    datasets: [
      {
        label: '월별 판매액',
        data: data.map((item) => item.sales),
        borderColor: '#42a5f5',
        backgroundColor: 'rgba(66, 165, 245, 0.2)',
        fill: true,
      },
    ],
  };

  const options = {
    responsive: true,
    scales: {
      x: {
        beginAtZero: true,
      },
      y: {
        beginAtZero: true,
      },
    },
    plugins: {
      tooltip: {
        enabled: true,
      },
    },
  };

  return (
    <div className="chart-container">
      <h3>월별 판매 통계</h3>
      <Line data={chartData} options={options} />
    </div>
  );
};

export default MonthlySalesChart;
