import React from 'react';
import './SalesPrediction.css'; // CSS 파일 임포트

const SalesPrediction = ({ predictedSales }) => {
  return (
    <div>
      <h1>7월 판매 예측</h1>
      {predictedSales !== null ? (
        <p>예측된 7월 판매량: <span>{predictedSales.toLocaleString()} 원</span></p> // 판매량 강조
      ) : (
        <p className="loading">판매 예측을 불러오는 중입니다...</p> // 로딩 상태 스타일 적용
      )}
    </div>
  );
}

export default SalesPrediction;
