import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MonthlySalesChart from './MonthlySalesChart';  // 월별 판매 차트
import MonthlyOrdersChart from './MonthlyOrdersChart'; // 월별 주문 건수 차트
import CategorySalesChart from './CategorySalesChart'; // 카테고리별 판매 차트
import './DashboardPage.css';  // CSS 스타일

function DashboardPage() {
  const [salesData, setSalesData] = useState(null);
  const [ordersData, setOrdersData] = useState(null);
  const [categorySalesData, setCategorySalesData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        // 관리자 대시보드 데이터 API 호출
        const salesResponse = await axios.get('/admin/monthly-sales', {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
        });
        const ordersResponse = await axios.get('/admin/monthly-orders', {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
        });
        const categorySalesResponse = await axios.get('/admin/category-sales', {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
        });

        // 각 데이터 상태 업데이트
        setSalesData(salesResponse.data);
        setOrdersData(ordersResponse.data);
        setCategorySalesData(categorySalesResponse.data);
      } catch (err) {
        setError('데이터를 불러오는 중 오류가 발생했습니다.');
      }
    };

    fetchDashboardData();
  }, []);

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="dashboard-page-container">
      <h2>관리자 대시보드</h2>

      {/* 월별 판매 차트 */}
      <div className="chart-container">
        <h3>월별 판매 통계</h3>
        {salesData ? <MonthlySalesChart data={salesData} /> : <div>로딩 중...</div>}
      </div>

      {/* 월별 주문 건수 차트 */}
      <div className="chart-container">
        <h3>월별 주문 건수</h3>
        {ordersData ? <MonthlyOrdersChart data={ordersData} /> : <div>로딩 중...</div>}
      </div>

      {/* 카테고리별 판매 차트 */}
      <div className="chart-container">
        <h3>카테고리별 판매 통계</h3>
        {categorySalesData ? <CategorySalesChart data={categorySalesData} /> : <div>로딩 중...</div>}
      </div>
    </div>
  );
}

export default DashboardPage;
