import React, { createContext, useState, useEffect } from 'react';
import { logout as apiLogout } from '../api/auth';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUser = async () => {
      const token = localStorage.getItem('token');
      const storedUser = localStorage.getItem('user');  // localStorage에서 user 정보 가져오기

      if (token && storedUser) {
        try {
          const currentUser = JSON.parse(storedUser);  // 저장된 user 정보를 파싱하여 사용
          setUser(currentUser);  // 상태 업데이트
        } catch (error) {
          console.error('User info parsing error:', error);
          localStorage.removeItem('token');
          localStorage.removeItem('user');
          setUser(null);
        }
      }
      setLoading(false);
    };
    fetchUser();
  }, []);

  const logout = () => {
    apiLogout();
    localStorage.removeItem('token');  // token 삭제
    localStorage.removeItem('user');   // user 정보 삭제
    setUser(null);  // 상태 초기화
  };

  return (
    <AuthContext.Provider value={{ user, setUser, logout, loading }}>
      {children}
    </AuthContext.Provider>
  );
};
