import React from 'react';
import { HashRouter as Router, Routes, Route } from 'react-router-dom';

import Header from './components/Header';
import Footer from './components/Footer';
import PrivateRoute from './components/PrivateRoute';  // 권한 관리용 컴포넌트

import HomePage from './pages/HomePage';
import CartPage from './pages/CartPage';
import CheckoutPage from './pages/CheckoutPage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import AdminPage from './pages/AdminPage';
import AdminLoginPage from './pages/AdminLoginPage';
import DashboardPage from './dashboard/DashboardPage';

import './styles/global.css';

function App() {
  return (
    <Router>
      <Header />
      <main style={{ padding: '20px', minHeight: '80vh' }}>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/cart" element={<CartPage />} />
          <Route path="/checkout" element={<CheckoutPage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route path="/admin-login" element={<AdminLoginPage />} />

          {/* /admin 경로에 대한 리디렉션 추가 */}
          <Route
            path="/admin"
            element={<PrivateRoute adminOnly={true}><AdminPage /></PrivateRoute>}
          />

          {/* 관리자 대시보드 페이지 - /admin/dashboard-summary 경로 */}
          <Route
            path="/admin/dashboard-summary"
            element={
              <PrivateRoute adminOnly={true}>
                <AdminPage />
              </PrivateRoute>
            }
          />

          {/* 관리자 대시보드 페이지 - /admin/dashboard 경로 */}
          <Route
            path="/admin/dashboard"
            element={
              <PrivateRoute adminOnly={true}>
                <AdminPage />
              </PrivateRoute>
            }
          />
          <Route
            path="/admin/dashboard"
            element={
              <PrivateRoute adminOnly={true}>
                <DashboardPage />
              </PrivateRoute>
            }
/>


        </Routes>
      </main>
      <Footer />
    </Router>
  );
}

export default App;
