import React from 'react';
import { Bar } from 'react-chartjs-2'; // 막대 차트를 위한 Bar 컴포넌트
import 'chart.js/auto';

function GenderCategoryRanking({ data }) {
  // 성별별로 카테고리 데이터를 그룹화
  const groupedData = data.reduce((acc, item) => {
    if (!acc[item.gender]) {
      acc[item.gender] = [];
    }
    acc[item.gender].push(item);
    return acc;
  }, {});

  // 차트 데이터 준비
  const chartData = {
    labels: [],  // X축 라벨 (카테고리)
    datasets: []  // 막대 차트 데이터 세트
  };

  // 성별별로 각 카테고리 판매량을 추출
  const categories = Array.from(new Set(data.map(item => item.category)));  // 모든 카테고리 목록

  // 성별별로 카테고리 판매량을 수집하여 정렬
  const genders = ['F', 'M'];  // F: 여성, M: 남성

  genders.forEach(gender => {
    // 성별별 데이터 추출 후 카테고리별 판매량 정리
    const genderData = categories.map(category => {
      const categoryData = groupedData[gender].find(item => item.category === category);
      return categoryData ? categoryData.quantity : 0;  // 카테고리별 판매량, 없으면 0
    });

    chartData.datasets.push({
      label: gender === 'M' ? '남성' : '여성',
      data: genderData,
      backgroundColor: gender === 'M' ? '#FF6384' : '#36A2EB',
      hoverBackgroundColor: gender === 'M' ? '#FF6384' : '#36A2EB',
      borderWidth: 1
    });
  });

  // 차트 옵션 설정
  const options = {
    responsive: true,
    indexAxis: 'y',  // Y축에 막대를 배치하여 수평 막대 그래프 만들기
    scales: {
      x: {
        title: {
          display: true,
          text: '판매량'
        },
        beginAtZero: true,
      },
      y: {
        title: {
          display: true,
          text: '카테고리'
        },
      }
    },
    plugins: {
      legend: {
        position: 'top',
      }
    }
  };

  return (
    <div className="chart">
      <h3>성별 카테고리 판매 순위</h3>
      <Bar data={chartData} options={options} />
    </div>
  );
}

export default GenderCategoryRanking;
