import React from 'react';

// props로 monthlySales(1~6월 판매량)과 predictedSales(7월 예측된 판매량)를 받습니다
const SalesPrediction = ({ monthlySales = [], predictedSales }) => {
  return (
    <div>
      <h1>2025년 판매 예측</h1>
      <div>
        <h2>2025년 1월 ~ 6월 판매량</h2>
        <ul>
          {monthlySales.length > 0 ? (
            monthlySales.map((sales, index) => (
              <li key={index}>
                {index + 1}월 판매량: {sales.toLocaleString()} 원
              </li>
            ))
          ) : (
            <li>판매 데이터가 없습니다.</li>
          )}
        </ul>
      </div>
      <div>
        <h2>예측된 7월 판매량</h2>
        {predictedSales !== null ? (
          <p>예측된 7월 판매량: {predictedSales.toLocaleString()} 원</p>
        ) : (
          <p>판매 예측을 불러오는 중입니다...</p>
        )}
      </div>
    </div>
  );
}

export default SalesPrediction;
