import React from 'react';
import { Bar } from 'react-chartjs-2'; // 막대 차트를 위한 Bar 컴포넌트
import 'chart.js/auto';

function GenderCategoryRanking({ data }) {
  // 성별별로 카테고리 데이터를 그룹화
  const groupedData = data.reduce((acc, item) => {
    if (!acc[item.gender]) {
      acc[item.gender] = [];
    }
    acc[item.gender].push(item);
    return acc;
  }, {});

  // 모든 카테고리 목록을 가져오고 판매량 기준으로 정렬
  const sortedCategories = Array.from(new Set(data.map(item => item.category)))
    .map(category => {
      const totalQuantity = data
        .filter(item => item.category === category)
        .reduce((sum, item) => sum + item.quantity, 0);
      return { category, totalQuantity };
    })
    .sort((a, b) => b.totalQuantity - a.totalQuantity); // 판매량이 많은 순서대로 정렬

  // 차트 데이터 준비
  const chartData = {
    labels: sortedCategories.map(item => item.category),  // X축 라벨: 카테고리
    datasets: []  // 막대 차트 데이터 세트
  };

  // 성별별로 각 카테고리 판매량을 추출
  const genders = ['F', 'M'];  // F: 여성, M: 남성

  genders.forEach(gender => {
    // 성별별 데이터 추출 후 카테고리별 판매량 정리
    const genderData = sortedCategories.map(({ category }) => {
      const categoryData = groupedData[gender].find(item => item.category === category);
      return categoryData ? categoryData.quantity : 0;  // 카테고리별 판매량, 없으면 0
    });

    chartData.datasets.push({
      label: gender === 'M' ? '남성' : '여성',
      data: genderData,
      backgroundColor: gender === 'M' ? '#36A2EB' : '#FF6384',  // 남성 파랑, 여성 빨강
      hoverBackgroundColor: gender === 'M' ? '#36A2EB' : '#FF6384',
      borderWidth: 1
    });
  });

  // 차트 옵션 설정
  const options = {
    responsive: true,
    indexAxis: 'x',  // 세로형 막대 그래프
    scales: {
      x: {
        title: {
          display: true,
          text: '판매량'
        },
        beginAtZero: true,
      },
      y: {
        title: {
          display: true,
          text: '카테고리'
        },
      }
    },
    plugins: {
      legend: {
        position: 'top',
      }
    }
  };

  // 여성 순위 및 남성 순위를 텍스트로 추출
  const femaleRanking = sortedCategories
    .filter(item => groupedData['F'].some(data => data.category === item.category))
    .slice(0, 6) // 상위 6개만
    .map((item, index) => `${index + 1}. ${item.category}`).join(', ');

  const maleRanking = sortedCategories
    .filter(item => groupedData['M'].some(data => data.category === item.category))
    .slice(0, 6) // 상위 6개만
    .map((item, index) => `${index + 1}. ${item.category}`).join(', ');

  return (
    <div className="chart">
      <h3>성별 카테고리 판매 순위</h3>

      {/* 여성 순위 텍스트 */}
      <div style={{ marginBottom: '10px', fontSize: '16px', fontWeight: 'bold' }}>
        <span>여성 순위: {femaleRanking}</span>
      </div>

      {/* 남성 순위 텍스트 */}
      <div style={{ marginBottom: '20px', fontSize: '16px', fontWeight: 'bold' }}>
        <span>남성 순위: {maleRanking}</span>
      </div>

      {/* 차트 */}
      <Bar data={chartData} options={options} />
    </div>
  );
}

export default GenderCategoryRanking;
