import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminPage.css';

function AdminPage() {
  const [salesData, setSalesData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSalesData = async () => {
      try {
        const response = await axios.get('http://localhost:8080/admin/admin/dashboard-summary', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`  // Authorization 헤더에 토큰 추가
          }
        });
        setSalesData(response.data);
      } catch (err) {
        setError('데이터를 불러오는 중 오류가 발생했습니다.');
      }
    };

    fetchSalesData();  // 페이지 로드시 데이터 호출
  }, []);

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h3>월별 판매 통계</h3>
      {salesData ? <pre>{JSON.stringify(salesData, null, 2)}</pre> : <div>로딩 중...</div>}
    </div>
  );
}

export default AdminPage;
