import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import ProductCard from '../components/ProductCard';
import './HomePage.css';
import { fetchProducts } from '../api/products';

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

function HomePage() {
  const location = useLocation();
  const query = useQuery();
  const category = query.get('category');
  const navigate = useNavigate();

  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  // 새로 추가한 상태: 선택한 상품 ID 배열
  const [selectedProducts, setSelectedProducts] = useState([]);

  useEffect(() => {
    setLoading(true);
    fetchProducts(category)
      .then(data => {
        setProducts(data);
        setSelectedProducts([]);  // 카테고리 바뀌면 선택 초기화
      })
      .catch(() => {
        setProducts([]);
        setSelectedProducts([]);
      })
      .finally(() => setLoading(false));
  }, [category, location.search]);

  const handleCheckboxChange = (productId) => {
    setSelectedProducts(prev => {
      if (prev.includes(productId)) {
        return prev.filter(id => id !== productId);
      } else {
        return [...prev, productId];
      }
    });
  };

  const handleAddToCart = () => {
    if (selectedProducts.length === 0) {
      alert('상품을 선택해주세요.');
      return;
    }

    const storedCart = JSON.parse(localStorage.getItem('cart')) || [];

    // 선택된 상품 정보 필터링
    const productsToAdd = products
      .filter(p => selectedProducts.includes(p.id))
      .map(p => ({ product: p, quantity: 1 }));

    // 장바구니에 중복 상품 처리 로직이 필요하면 여기서 작성 가능
    const newCart = [...storedCart, ...productsToAdd];

    localStorage.setItem('cart', JSON.stringify(newCart));
    alert('선택한 상품이 장바구니에 추가되었습니다.');
    navigate('/cart');
  };

  if (loading) return <p>상품을 불러오는 중입니다...</p>;
  if (products.length === 0) return <p>해당 카테고리의 상품이 없습니다.</p>;

  return (
    <div className="products-container">
      {products.map(product => (
        <div key={product.id} style={{ display: 'flex', alignItems: 'center' }}>
          <input
            type="checkbox"
            checked={selectedProducts.includes(product.id)}
            onChange={() => handleCheckboxChange(product.id)}
            style={{ marginRight: '10px' }}
          />
          <ProductCard product={product} />
        </div>
      ))}

      <button onClick={handleAddToCart} style={{ marginTop: '20px' }}>
        선택 상품 장바구니에 담기
      </button>
    </div>
  );
}

export default HomePage;
