import React from 'react';
import { Pie } from 'react-chartjs-2';
import 'chart.js/auto';

function GenderCategoryRanking({ data }) {
  // 성별별로 카테고리 데이터를 그룹화
  const groupedData = data.reduce((acc, item) => {
    if (!acc[item.gender]) {
      acc[item.gender] = [];
    }
    acc[item.gender].push(item);
    return acc;
  }, {});

  // 차트 데이터 준비
  const chartData = {
    labels: [],
    datasets: []
  };

  // 성별별로 가장 많이 팔린 카테고리와 판매량 추출
  Object.keys(groupedData).forEach(gender => {
    // 각 성별에 대해 카테고리별로 quantity를 기준으로 내림차순 정렬
    const sortedData = groupedData[gender].sort((a, b) => b.quantity - a.quantity);

    // 가장 많이 팔린 카테고리만 선택
    const topCategory = sortedData[0];

    // 성별 라벨 추가 (F -> 여성, M -> 남성으로 변경)
    const genderLabel = gender === 'M' ? '남성' : '여성';
    chartData.labels.push(`${genderLabel} - ${topCategory.category}`);

    // 판매량 데이터 추가
    chartData.datasets.push({
      label: `${genderLabel} - ${topCategory.category}`,
      data: [topCategory.quantity],
      backgroundColor: gender === 'M' ? '#FF6384' : '#36A2EB',  // 성별에 따른 색상
      hoverBackgroundColor: gender === 'M' ? '#FF6384' : '#36A2EB',
    });
  });

  return (
    <div className="chart">
      <h3>성별 카테고리 판매 순위</h3>
      <Pie data={chartData} />
    </div>
  );
}

export default GenderCategoryRanking;
