import React, { useEffect, useState } from 'react';
import { getOCRResults, getSalesData } from '../api/ocr';
import './AdminPage.css';

function AdminPage() {
  const [ocrResults, setOcrResults] = useState([]);
  const [salesData, setSalesData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        setLoading(true);
        const [ocrRes, salesRes] = await Promise.all([
          getOCRResults(),
          getSalesData()
        ]);
        setOcrResults(ocrRes.data);
        setSalesData(salesRes.data);
      } catch (err) {
        setError('데이터를 불러오는 중 오류가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    }
    fetchData();
  }, []);

  if (loading) return <div>로딩 중...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="admin-page">
      <h2>관리자 대시보드</h2>

      <section className="sales-section">
        <h3>매출 현황</h3>
        {salesData ? (
          <div>
            <p>총 매출: {salesData.totalSales} 원</p>
            <p>주문 건수: {salesData.orderCount} 건</p>
            {/* 필요시 더 상세 내역 추가 */}
          </div>
        ) : (
          <p>매출 데이터가 없습니다.</p>
        )}
      </section>

      <section className="ocr-results-section">
        <h3>OCR 결과 목록</h3>
        {ocrResults.length > 0 ? (
          <ul className="ocr-results-list">
            {ocrResults.map(item => (
              <li key={item.id}>
                <p>이미지명: {item.imageName}</p>
                <p>결과: {item.text}</p>
                <p>처리일: {new Date(item.processedAt).toLocaleString()}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>OCR 결과가 없습니다.</p>
        )}
      </section>
    </div>
  );
}

export default AdminPage;
