import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getProducts } from '../api/products'; // 상품 불러오는 API 함수 가정
import './HomePage.css';

function HomePage() {
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // 상품 목록 API 호출
    async function fetchProducts() {
      const data = await getProducts();
      setProducts(data);
    }
    fetchProducts();
  }, []);

  const handleCheckboxChange = (productId) => {
    if (selectedProducts.includes(productId)) {
      setSelectedProducts(selectedProducts.filter(id => id !== productId));
    } else {
      setSelectedProducts([...selectedProducts, productId]);
    }
  };

  const handleAddToCart = () => {
    if (selectedProducts.length === 0) {
      alert('상품을 선택하세요.');
      return;
    }

    // 기존 장바구니 데이터 불러오기
    const storedCart = JSON.parse(localStorage.getItem('cart')) || [];

    // 선택된 상품들 상세정보 가져오기 (여기선 products 배열에서 필터링)
    const productsToAdd = products.filter(p => selectedProducts.includes(p.id))
      .map(p => ({ product: p, quantity: 1 })); // 수량 1로 기본 설정

    // 장바구니에 중복 상품 처리할지 여부에 따라 로직 추가 가능
    const newCart = [...storedCart, ...productsToAdd];

    localStorage.setItem('cart', JSON.stringify(newCart));
    alert('선택한 상품이 장바구니에 추가되었습니다.');
    navigate('/cart');
  };

  return (
    <div className="home-container">
      <h2>상품 목록</h2>
      <ul>
        {products.map(product => (
          <li key={product.id}>
            <input
              type="checkbox"
              checked={selectedProducts.includes(product.id)}
              onChange={() => handleCheckboxChange(product.id)}
            />
            <img src={product.imageUrl} alt={product.name} width="50" />
            {product.name} - {product.price}원
          </li>
        ))}
      </ul>
      <button onClick={handleAddToCart}>선택 상품 장바구니에 담기</button>
    </div>
  );
}

export default HomePage;
